'  l_accounts_found BOOLEAN := FALSE;',
'BEGIN',
'  l_html := ''''; -- Initialize CLOB',
'',
'  IF :APP_USER IS NOT NULL THEN',
'    -- Start the main profile container div',
'    l_html := l_html || ''<div class="profile-container">'';',
