'      -- Customer Details Section',
'      l_html := l_html || ''<h3>Hello, '' || APEX_ESCAPE.HTML(l_customer_rec.full_name) || ''!</h3>'';',
'      l_html := l_html || ''<div class="customer-details-list">'';',
'      l_html := l_html || ''<p>Here are your profile details:</p>'';',
